mapper.update:
update lesson_group
set name=#{name},
    grade_id = #{gradeId},
    subject_id = #{subjectId},
    version = #{version}
where id = #{id}

mapper.select:
select id, name, grade_id, subject_id
from lesson_group
where id = #{id}

select count(id)
from lesson_group
where subject_id = #{subjectId}

select count(id)
from lesson_group
where grade_id = #{gradeId}

select g.id,g.name,g.version,u.id as user_id,u.name as user_name,g.grade_id,g.subject_id,concat(g2.name,s.name) as lesson_name,u.group_leader_flag from lesson_group g left join group_user gu on g.id = gu.group_id left join user u on gu.user_id = u.id left join grade g2 on g.grade_id = g2.id left join subject s on g.subject_id = s.id
and g.id = #{id}
(u.name like #{_name} or u.job_number like #{_name})
